mapper.select:
SELECT *
FROM (
SELECT ROWNUM RN, NOTICE.*
FROM (
SELECT *
FROM MasterNotice
ORDER BY mnReg DESC
) NOTICE
)
WHERE RN BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE MEMBER SET
mEmail=#{mEmail},
mPwd = #{mPwd}, mName = #{mName},
mPhone = #{mPhone}, mPost = #{mPost}, mAddress = #{mAddress},
mGrade = #{mGrade}, mReg = #{mReg}, mDrop = #{mDrop}
where mEmail = #{mEmail}
